  test "test help modal" do
    assert render_component(&render_help_modal/1) ==  
    "<div id=\"help_modal\" phx-remove=\"[[&quot;hide&quot;,{&quot;time&quot;:200,&quot;to&quot;:&quot;#help_modal-bg&quot;,&quot;transition&quot;:[[&quot;transition-all&quot;,&quot;transform&quot;,&quot;ease-in&quot;,&quot;duration-200&quot;],[&quot;opacity-100&quot;],[&quot;opacity-0&quot;]]}],[&quot;hide&quot;,{&quot;time&quot;:200,&quot;to&quot;:&quot;#help_modal-container&quot;,&quot;transition&quot;:[[&quot;transition-all&quot;,&quot;transform&quot;,&quot;ease-in&quot;,&quot;duration-200&quot;],[&quot;opacity-100&quot;,&quot;translate-y-0&quot;,&quot;sm:scale-100&quot;],[&quot;opacity-0&quot;,&quot;translate-y-4&quot;,&quot;sm:translate-y-0&quot;,&quot;sm:scale-95&quot;]]}],[&quot;hide&quot;,{&quot;time&quot;:200,&quot;to&quot;:&quot;#help_modal&quot;,&quot;transition&quot;:[[&quot;block&quot;],[&quot;block&quot;],[&quot;hidden&quot;]]}],[&quot;remove_class&quot;,{&quot;time&quot;:200,&quot;names&quot;:[&quot;overflow-hidden&quot;],&quot;to&quot;:&quot;body&quot;,&quot;transition&quot;:[[],[],[]]}],[&quot;pop_focus&quot;,{}]]\" data-cancel=\"[[&quot;exec&quot;,[&quot;phx-remove&quot;]]]\" class=\"relative z-50 hidden\">\n  <div id=\"help_modal-bg\" class=\"bg-zinc-50/90 fixed inset-0 transition-opacity\" aria-hidden=\"true\"></div>\n  <div class=\"fixed inset-0 overflow-y-auto\" aria-labelledby=\"help_modal-title\" aria-describedby=\"help_modal-description\" role=\"dialog\" aria-modal=\"true\" tabindex=\"0\">\n    <div class=\"flex min-h-full items-center justify-center\">\n      <div class=\"w-full max-w-3xl p-4 sm:p-6 lg:py-8\">\n        <div id=\"help_modal-container\" phx-hook=\"Phoenix.FocusWrap\" class=\"shadow-zinc-700/10 ring-zinc-700/10 relative hidden rounded-2xl bg-white p-14 shadow-lg ring-1 transition\" phx-click-away=\"[[&quot;exec&quot;,[&quot;data-cancel&quot;,&quot;#help_modal&quot;]]]\" phx-key=\"escape\" phx-window-keydown=\"[[&quot;exec&quot;,[&quot;data-cancel&quot;,&quot;#help_modal&quot;]]]\">\n  <span id=\"help_modal-container-start\" tabindex=\"0\" aria-hidden=\"true\"></span>\n  \n          <div class=\"absolute top-6 right-5\">\n            <button phx-click=\"[[&quot;exec&quot;,[&quot;data-cancel&quot;,&quot;#help_modal&quot;]]]\" type=\"button\" class=\"-m-3 flex-none p-3 opacity-20 hover:opacity-40\" aria-label=\"close\">\n              <span class=\"hero-x-mark-solid h-5 w-5\"></span>\n            </button>\n          </div>\n          <div id=\"help_modal-content\">\n            \n    <span class=\"text:lg font-bold\">\n    <p>Rules of Towers of Hanoi</p></span>\n    <span class=\"prose\">\n    <p>There are three piles of stones. You start with all the stones\n    on the left pile. The goal is to move all the stones to the right\n    pile.</p>\n    <ul class=\"list-disc list-inside\">\n      <li>Stones must be moved one at a time.</li>\n      <li>A larger stone cannot be placed on a smaller one.</li>\n    </ul>\n    <p>The goal is to complete the task in the smallest number\n    of moves.</p>\n    </span>\n  \n          </div>\n        \n  <span id=\"help_modal-container-end\" tabindex=\"0\" aria-hidden=\"true\"></span>\n</div>\n      </div>\n    </div>\n  </div>\n</div>"
  end
end
